import React from 'react'
import styled from 'styled-components'

const Container = styled.div`
    height: 30px;
    background-color: teal;
    color: white;
    display: flex;
    justify-content: center;
    align-items: center;
    font-size: 14px;
`
const Announcement = () => {
  return (
    <Container>
        Super Deal! Free Shopping on Orders Over $50
    </Container>
  )
}

export default Announcement
